import{_ as i}from"./plugin-vue_export-helper-c27b6911.js";import{r as n,o as h,c as l,a as s,b as a,d as r,e as d}from"./app-07f5e5d2.js";const o="/assets/library_floors-cbf22ef7.png",p="/assets/library_1-290dc20c.jpg",c="/assets/library_2-b9fa9c78.jpeg",f="/assets/library_3-9fda223a.jpg",u={},b={href:"https://lib.hfut.edu.cn",target:"_blank",rel:"noopener noreferrer"},g={href:"https://xclib.hfut.edu.cn",target:"_blank",rel:"noopener noreferrer"};function x(_,t){const e=n("ExternalLinkIcon");return h(),l("div",null,[t[2]||(t[2]=s('<h1 id="图书馆" tabindex="-1"><a class="header-anchor" href="#图书馆" aria-hidden="true">#</a> 图书馆</h1><p>宣城校区的地标性建筑，有阅览座位 3300 余席、休闲座位 360 席、电子阅览室座位 144 席、研讨间 20 个。</p><h2 id="开放时间" tabindex="-1"><a class="header-anchor" href="#开放时间" aria-hidden="true">#</a> 开放时间</h2><table><thead><tr><th>功能</th><th>时间段</th></tr></thead><tbody><tr><td>图书馆开放</td><td>7:30 - 22:15</td></tr><tr><td>图刊借阅</td><td>8:00 - 22:00</td></tr><tr><td>电子阅览室</td><td>8:00 - 22:00</td></tr><tr><td>研讨间</td><td>8:10 - 22:00</td></tr><tr><td>报刊阅览</td><td>8:00 - 22:00</td></tr></tbody></table><h2 id="楼层" tabindex="-1"><a class="header-anchor" href="#楼层" aria-hidden="true">#</a> 楼层</h2><figure><img src="'+o+'" alt="图书馆楼层指示" width="300" tabindex="0" loading="lazy"><figcaption>图书馆楼层指示</figcaption></figure><p>校史馆在第十层。</p><h2 id="图书借阅" tabindex="-1"><a class="header-anchor" href="#图书借阅" aria-hidden="true">#</a> 图书借阅</h2><p>书籍检索可以通过校图书馆网站查找，搜索书名、作者等，然后通过索书号找到对应楼层的对应书架。馆内阅读完毕可以将书放回原处或放到每层楼的小推车处（请不要随手扔在桌上或是随意插到某个书架里），若需要带离馆外，则需要到图书馆二楼自主借书机器刷卡，还书时可到二楼借书机或一楼北面24小时还书机处。</p><p>若有屯溪路校区图书馆藏书借阅需求，可咨询服务台。当然，如果你在合肥两校区内，也是可以自由借阅的，但需要自行归还。</p><h2 id="物品存放" tabindex="-1"><a class="header-anchor" href="#物品存放" aria-hidden="true">#</a> 物品存放</h2><p>每层楼都有储物柜，可以通过刷卡存取物品，其中第一次刷卡为存，第二次刷卡为取，只有这两次保证能刷到同样的柜子。超过48小时存放的物品会被移至二楼服务台后的架子上，贵重物品由服务台保管。若有物品遗失，建议先联系服务台。</p><h2 id="空调温度" tabindex="-1"><a class="header-anchor" href="#空调温度" aria-hidden="true">#</a> 空调温度</h2><p>通常空调温度为楼层越高，温度越低。</p><h2 id="电子阅览室" tabindex="-1"><a class="header-anchor" href="#电子阅览室" aria-hidden="true">#</a> 电子阅览室</h2><p>7楼电子阅览室提供免费校园网服务。</p><h2 id="研讨间" tabindex="-1"><a class="header-anchor" href="#研讨间" aria-hidden="true">#</a> 研讨间</h2><p>研讨间提供一个学习讨论的环境，可以在服务台办理借用，必须多人到场。</p><h2 id="打印机" tabindex="-1"><a class="header-anchor" href="#打印机" aria-hidden="true">#</a> 打印机</h2><p>自助文档打印可以通过2楼服务台的电脑，或者7楼电脑，亦或自行在电脑安装自助打印程序并连接校园网，上传打印文档。默认密码为学号。上传后，在服务台两台机器之一刷卡扣费即可。可打印A4和A3，也可以进行扫描、复印操作。</p><p>在读证明、成绩单、四六级成绩证明等都可以通过2楼大厅中间的打印机打印（或敬亭新安正门附近、行政楼也有），每人有一定免费额度。</p><h2 id="_104办公室-勤工助学中心" tabindex="-1"><a class="header-anchor" href="#_104办公室-勤工助学中心" aria-hidden="true">#</a> 104办公室（勤工助学中心）</h2><p>勤工助学相关办公室，入口为图书馆外的西侧。</p><h2 id="网站" tabindex="-1"><a class="header-anchor" href="#网站" aria-hidden="true">#</a> 网站</h2>',24)),a("ul",null,[a("li",null,[a("a",b,[t[0]||(t[0]=r("总馆")),d(e)])]),a("li",null,[a("a",g,[t[1]||(t[1]=r("宣区图书馆")),d(e)])])]),t[3]||(t[3]=a("figure",null,[a("img",{src:p,alt:"图书馆",width:"300",tabindex:"0",loading:"lazy"}),a("figcaption",null,"图书馆")],-1)),t[4]||(t[4]=a("figure",null,[a("img",{src:c,alt:"图书馆",width:"300",tabindex:"0",loading:"lazy"}),a("figcaption",null,"图书馆")],-1)),t[5]||(t[5]=a("figure",null,[a("img",{src:f,alt:"阅览区",width:"300",tabindex:"0",loading:"lazy"}),a("figcaption",null,"阅览区")],-1))])}const k=i(u,[["render",x],["__file","library.html.vue"]]);export{k as default};
